document.addEventListener('DOMContentLoaded', function () {
  /** (Start) Показываем/Скрываем Кнопку Play и атрибут controls **/
  var videoWrappers = document.querySelectorAll('.block-video-wrapper');
  videoWrappers.forEach(function (wrapper) {
    var video = wrapper.querySelector('video');
    var playIcon = wrapper.querySelector('.icon-video-play');
    video.addEventListener('play', function () {
      playIcon.classList.add('hide');
      video.setAttribute('controls', '');
    });
    video.addEventListener('pause', function () {
      playIcon.classList.remove('hide');
      video.removeAttribute('controls');
    });
  });
  /** (End) Показываем/Скрываем Кнопку Play и атрибут controls **/

  /** (Start) Динамическая ширина Input Quantity **/
  var inputQuantities = document.querySelectorAll('.quantity-current');
  inputQuantities.forEach(function (inputQuantity) {
    inputQuantity.addEventListener('input', function () {
      var value = inputQuantity.value;
      var length = value.length;
      var width = length + 'ch';
      inputQuantity.style.width = width;
    });
  });

  /** (End) Динамическая ширина Input Quantity **/
});
